import * as Yup from "yup";

const locationSchema = Yup.object().shape({
  locationTypeId: Yup.number()
    .oneOf([2, 3, 4, 5, 6], "Please select a Location Type")
    .required(),
  lineOne: Yup.string().required("This field is required."),
  lineTwo: Yup.string(),
  city: Yup.string().required("This field is required."),
  //Question - stateId is a number, so how do I set that field up correctly?
  state: Yup.string().required("This field is required."),
  zip: Yup.string().required("This field is required."),
  country: Yup.string().required("This field is required.")
});

const locationSchemaV2 = Yup.object().shape({
  locationTypeId: Yup.number()
    .oneOf([2, 3, 4, 5, 6], "Please select a Location Type")
    .required(),
  lineOne: Yup.string().required("This field is required."),
  lineTwo: Yup.string(),
  city: Yup.string().required("This field is required."),
  //Question - stateId is a number, so how do I set that field up correctly?
  stateId: Yup.number().required("This field is required."),
  zip: Yup.string().required("This field is required.")
});

const forgotPasswordSchema = Yup.object().shape({
  email: Yup.string()
    .email("This value should be a valid email.")
    .required("This value is required.")
});

export { locationSchema, locationSchemaV2 };
