import React from "react";
import PropTypes from "prop-types";
import { Formik, Form, Field, ErrorMessage } from "formik";
import { locationSchema } from "../validationSchema";
import { FormGroup, Label, Button } from "reactstrap";
import PlacesAutocomplete from "react-places-autocomplete";
// import "./LocationsStyles.css";

const AddressAC = props => {
  return (
    <>
      <PlacesAutocomplete
        value={props.address}
        onChange={props.handleAddressChange}
        onSelect={props.handleAddressSelect}
      >
        {({ getInputProps, suggestions, getSuggestionItemProps, loading }) => (
          <div style={{ height: "auto", width: "100%" }}>
            <input
              {...getInputProps({
                placeholder: "Search Addresses ...",
                className: "location-search-input"
              })}
            />
            <div className="autocomplete-dropdown-container">
              {loading && <div>Loading...</div>}
              {suggestions.map(suggestion => {
                const className = suggestion.active
                  ? "suggestion-item--active"
                  : "suggestion-item";
                // inline style for demonstration purpose
                const style = suggestion.active
                  ? { backgroundColor: "#fafafa", cursor: "pointer" }
                  : { backgroundColor: "#ffffff", cursor: "pointer" };
                return (
                  <div
                    key={suggestion.index}
                    {...getSuggestionItemProps(suggestion, {
                      className,
                      style
                    })}
                  >
                    <span>{suggestion.description}</span>
                  </div>
                );
              })}
            </div>
          </div>
        )}
      </PlacesAutocomplete>

      <Formik
        enableReinitialize={true} // CRITICAL
        initialValues={props.location}
        onSubmit={props.handleSubmit}
        validationSchema={locationSchema}
      >
        <Form>
          <FormGroup>
            <Label for="locationTypeId">Location Type</Label>
            <Field
              // type="select"
              className="form-control"
              name="locationTypeId"
              id="locationTypeId"
              placeholder="Location Type"
              component="select"
            >
              {/* {props.locationTypes.map(item => {
                return (
                  <option key={item.id} value={Number(item.id)}>
                    {item.name}
                  </option>
                );
              })} */}
            </Field>
          </FormGroup>

          <FormGroup>
            <Label for="location">Location Name</Label>
            <Field
              type="text"
              className="form-control"
              name="name"
              id="name"
              placeholder="Location Name"
            />
            <ErrorMessage name="name" />
          </FormGroup>

          <FormGroup>
            <Label for="lineOne">Address Line 1</Label>
            <Field
              type="text"
              className="form-control"
              name="lineOne"
              id="lineOne"
              placeholder="Address Line 1"
            />
            <ErrorMessage name="lineOne" />
          </FormGroup>

          <FormGroup>
            <Label for="lineTwo">Address Line 2</Label>
            <Field
              type="text"
              className="form-control"
              name="lineTwo"
              id="lineTwo"
              placeholder="Address Line 2"
            />
            <ErrorMessage name="lineTwo" />
          </FormGroup>

          <FormGroup>
            <Label for="city">City</Label>
            <Field
              type="text"
              className="form-control"
              name="city"
              id="city"
              placeholder="City"
            />
            <ErrorMessage name="city" />
          </FormGroup>

          <FormGroup>
            <Label for="stateId">State</Label>
            <Field
              // type="select"
              className="form-control"
              name="stateId"
              id="stateId"
              placeholder="State"
              component="select"
            >
              {props.locationStates.map(state => {
                return (
                  <option key={state.id} value={Number(state.id)}>
                    {state.name}
                  </option>
                );
              })}

              <ErrorMessage name="name" />
            </Field>
          </FormGroup>

          <FormGroup>
            <Label for="zip">Zip</Label>
            <Field
              type="text"
              className="form-control"
              name="zip"
              id="zip"
              placeholder="Zip Code"
            />
            <ErrorMessage name="zip" />
          </FormGroup>

          <Button
            className="btn btn-secondary btn-sm float-left"
            type="button"
            onClick={props.clearForm}
          >
            Clear Form
          </Button>
          <Button className="btn btn-primary btn-sm float-right" type="submit">
            Submit
          </Button>
          <Button
            className="btn btn-primary btn-sm float-right"
            type="button"
            onClick={props.showLocations}
          >
            Cancel
          </Button>
        </Form>
      </Formik>
    </>
  );
};

AddressAC.propTypes = {
  location: PropTypes.shape({
    locationTypeId: PropTypes.number,
    name: PropTypes.string,
    lineOne: PropTypes.string,
    lineTwo: PropTypes.string,
    city: PropTypes.string,
    stateId: PropTypes.number,
    zip: PropTypes.string
  }),
  locationTypes: PropTypes.array,
  locationStates: PropTypes.array,
  handleSubmit: PropTypes.func,
  showLocations: PropTypes.func,
  isEditing: PropTypes.bool,
  handleAddressChange: PropTypes.func,
  handleAddressSelect: PropTypes.func,
  address: PropTypes.string,
  getSuggestionItemProps: PropTypes.func,
  suggestion: PropTypes.arrayOf(PropTypes.string),
  handleCheck: PropTypes.func,
  isSubmitted: PropTypes.bool,
  clearForm: PropTypes.func
};

export default AddressAC;
